mapper.insert:

INSERT INTO account (
	team_id
	, ap
	, cash
	, crt_date
	, upd_date
) values ( 
 	#{teamId}
 	, #{ap}
 	, #{cash}
 	, current_timestamp
 	, current_timestamp
 )

mapper.update:
UPDATE account
SET	upd_date = current_timestamp
	, ap = #{ap}
WHERE team_id = #{teamId}

UPDATE account
SET	upd_date = current_timestamp
	, cash = #{cash}
WHERE team_id = #{teamId}
