mapper.insert:

INSERT INTO player (
	player_id
	, person_id
	, team_code
	, cost
	, crt_date
	, upd_date
) values ( 
 	#{playerId}
 	, #{personId}
 	, #{teamCode}
 	, #{cost}
 	, current_timestamp
 	, current_timestamp
 )

mapper.update:
UPDATE player
SET upd_date = current_timestamp
	, cost = #{cost}
	, team_code = #{teamCode}
WHERE player_id = #{playerId};
