mapper.insert:

INSERT INTO team_player (
	team_id
	, player_id
	, level
	, exp
	, crt_date
	, upd_date
) values ( 
 	#{teamId}
 	, #{playerId}
 	, #{level}
 	, #{exp}
 	, current_timestamp
 	, current_timestamp
 );

mapper.update:
UPDATE team_player
SET upd_date = current_timestamp
	, level = #{level}
	, exp = #{exp}
WHERE team_id = #{teamId}
	AND player_id = #{playerId};
