mapper.insert:

INSERT INTO team_sr_slot (
	team_id
	, slot_no
	, player_id
	, contract_yn
	, crt_date
	, upd_date
) values ( 
 	#{teamId}
 	, #{slotNo}
 	, #{playerId}
 	, #{contractYN}
 	, current_timestamp
 	, current_timestamp
 )

mapper.update:
UPDATE team_sr_slot
SET upd_date = current_timestamp
	, player_id = #{playerId}
	, contract_yn = #{contractYN}
WHERE team_id = #{teamId}
	AND slot_no = #{slotNo};

mapper.select:
SELECT *
FROM team_sr_slot
WHERE team_id = #{teamId}
	AND slot_no = #{slotNo};
